mapper.select:
SELECT COUNT(*)
FROM MEMBER
WHERE MEMBER_NO = #{searchText}

mapper.update:
UPDATE "MEMBER"
SET MEMBER_DEL_FL = CASE WHEN MEMBER_DEL_FL = 'Y' THEN 'N' ELSE 'Y' END
WHERE MEMBER_NO = #{memberNo}

UPDATE "MEMBER"
SET MEMBER_FL = CASE WHEN MEMBER_FL = '1' THEN '2' ELSE '1' END
WHERE MEMBER_NO = #{memberNo}
