#{item}
)
INTERSECT
( SELECT DEVICE_NO, DEVICE_NAME, DEVICE_BUYING_PRICE, DEVICE_BRAND, DEVICE_RELEASE_DATE FROM RAM JOIN DEVICE USING (DEVICE_RAM_CODE) WHERE DEVICE_RAM_CAPACITY IN
#{item}
)
INTERSECT
( SELECT DEVICE_NO, DEVICE_NAME, DEVICE_BUYING_PRICE, DEVICE_BRAND, DEVICE_RELEASE_DATE FROM DISPLAY JOIN DEVICE USING (DEVICE_DISPLAY_CODE) WHERE DEVICE_DISPLAY_SIZE IN
#{item}
)
)
WHERE DEVICE_BRAND = #{brand} ORDER BY DEVICE_RELEASE_DATE DESC
